mapper.select:
select *
from restaurant


		category_id = #{categoryId};


		name like '%${query}%'



	limit #{page}, #{size}

select distinct restaurant_card.* from restaurant_card left join menu on restaurant_card.id = menu.restaurant_id
category_id = #{categoryId}
restaurant_card.name like '%${query}%' or menu.name like '%${query}%'

SELECT *
from restaurant_card
where category_id = ${categoryId}
ORDER BY avg_rated_price DESC;
